import React, { useState } from 'react';
import { BasketItem } from './BasketItem';
import { OrderForm } from './OrderForm';  // Предположим, что у вас есть компонент для формы заказа

function BasketList(props) {
  const {
    order = [],
    handleBasketShow = Function.prototype,
    removeFromBasket = Function.prototype,
    incQuantity,
    decQuantity,
  } = props;

  const totalPrice = order.reduce((sum, el) => {
    return sum + el.price * el.quantity;
  }, 0);

  // Состояние для отображения/скрытия формы заказа
  const [isOrderFormVisible, setIsOrderFormVisible] = useState(false);

  const handleOrderButtonClick = () => {
    // При нажатии на кнопку "Оформить заказ" изменить состояние isOrderFormVisible на true
    setIsOrderFormVisible(true);
  };

  return (
    <div>
      <ul className="collection basket-list">
        <li className="collection-item active blue darken-1">Кошик</li>
        {order.length ? (
          order.map((item) => (
            <BasketItem
              key={item.id}
              removeFromBasket={removeFromBasket}
              incQuantity={incQuantity}
              decQuantity={decQuantity}
              {...item}
            />
          ))
        ) : (
          <li className="collection-item">Кошик пустий</li>
        )}
        <li className="collection-item active blue darken-1">Загальна сума: {totalPrice} Грн. </li>
        <li className="collection-item center">
          {/* Добавляем обработчик события для кнопки "Оформить заказ" */}
          <button className="btn blue accent-4" onClick={handleOrderButtonClick}>
            Оформити замовлення
          </button>
        </li>
        <i className="material-icons basket-close" onClick={handleBasketShow}>
          clear
        </i>
      </ul>

      {/* Отображаем компонент формы заказа, если isOrderFormVisible равно true */}
      {isOrderFormVisible && <OrderForm />}
    </div>
  );
}

export { BasketList };
