// Импорт изображений (предположим, что изображения хранятся в папке assets)
import phoneImage from '../assets/phone.jpg';
import laptopImage from '../assets/laptop.jpg';
import headphonesImage from '../assets/headphones.jpg';
import usbDriveImage from '../assets/usb-drive.jpg';

// Объект с товарами
const initialGoods = [
  {
    id: 1,
    name: 'Смартфон',
    price: 500,
    image: phoneImage,
  },
  {
    id: 2,
    name: 'Ноутбук',
    price: 1200,
    image: laptopImage,
  },
  {
    id: 3,
    name: 'Наушники',
    price: 100,
    image: headphonesImage,
  },
  {
    id: 4,
    name: 'Флешка',
    price: 20,
    image: usbDriveImage,
  },
  // Дополнительные товары...
];

// Обновленный компонент GoodsList
function GoodsList(props) {
  const { goods = initialGoods, addToBasket = Function.prototype } = props;

  if (!goods.length) {
    return <h3>Nothing here</h3>;
  }

  return (
    <div className="goods">
      {goods.map((item) => (
        <GoodsItem key={item.id} {...item} addToBasket={addToBasket} />
      ))}
    </div>
  );
}

// Компонент GoodsItem (возможно, вам нужно дополнительно модифицировать его для отображения цен и изображений)
function GoodsItem(props) {
  const { id, name, addToBasket, price, image } = props;

  return (
    <div className="goods-item">
      <img src={image} alt={name} className="goods-item-image" />
      <h4>{name}</h4>
      <p>Цена: {price}₽</p>
      <button onClick={() => addToBasket({ id, name, price })}>Добавить в корзину</button>
    </div>
  );
}

export { GoodsList };
